      <TouchableOpacity 
      //TO DO: OnPress should clear the textboxes and disable the accept button (or just refresh the page entirely)
          onPress= {(myPrediction) => setMyPrediction(`__`)}
          style={{ backgroundColor: `rgba(203, 59, 59, 1)`}}>
