import 'react-native-gesture-handler'
import { useForm, Controller } from 'react-hook-form'
import { StatusBar } from 'expo-status-bar'
import React, { useEffect, useState, Component } from 'react'
import { StyleSheet, Button, Text, View, Alert, TextInput, TouchableOpacity, FlatList } from 'react-native'
import { NavigationContainer, ThemeProvider } from '@react-navigation/native'
import { createStackNavigator } from '@react-navigation/stack'
import _ from "lodash"
import { Grid, Row, Col } from 'react-flexbox-grid';

import { RecommendForm } from './RecommendForm'
import ReactDOM from 'react-dom'
import { State } from 'react-native-gesture-handler'

export default function App () {
  return (
	<NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Login" component={LoginScreen} />
        <Stack.Screen name="Signup" component={SignupScreen} />
        <Stack.Screen name="Main" component={MainScreen} 
          //Nulling headerLeft cuts off login screen altogether
          options={{/*headerLeft: null,*/ headerBackTitle: 'Log out'}}/>
        <Stack.Screen name="Recommendations" component={RecommendScreen} 
          options={{title: 'Get Recommendation'}}/>
        <Stack.Screen name="RecordDrive" component={RecordDriveScreen} 
          options={{title: 'Record Drive'}}/>
        <Stack.Screen name="SaveDrive" component={SaveDriveScreen} />
        <Stack.Screen name="Statistics" component={StatisticsScreen} />
      </Stack.Navigator>
  </NavigationContainer>
  )
}

const Stack = createStackNavigator()

// Login screen
function LoginScreen ({ navigation }) {

  // useForm allows us to validate inputs and build forms
  const {control, handleSubmit, setError, errors} = useForm( { criteriaMode: 'all' })
  const usernameInputRef = React.useRef()
  const passwordInputRef = React.useRef()

  const onSubmit = async (data) => { 
    // Once handleSubmit validates the inputs in onPress in button, this code is executed
    const json = JSON.parse(JSON.stringify(data))
    const username = json["username"]
    const password = json["password"]
    const user = {username, password}
    console.log(JSON.stringify(user))
    const response = await fetch("http://127.0.0.1:5000/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(user)
    }).then((response) => response.json())
    .then(data => {
        return data;
    });
    if (response.message == "login successful") {
      navigation.navigate('Main');
    } else {
      //clear the input fields and display message
      alert(`Login invalid`)
    }
    console.log(response)

  }
  const signup = () => {
    navigation.navigate('Signup');
  }

  const onError = (errors, e) => console.log(errors, e)

  return (
    <View style={styles.container}>
      <View>
        <Text style={styles.title}>Welcome!</Text>
        <Text style={styles.label}>Username</Text>
        <Controller 
          name="username" 
          control={control} 
          rules= {{required: 'This is required'}}
          defaultValue=''
          render={(props) => 
            <TextInput {...props} 
            //I know the following line sometimes gives a warning.
              //Please leave it in place, otherwise the forms are hard to work with
              autoCapitalize={false}
              style={styles.textbox}
              onChangeText={(value) => {
                props.onChange(value)
              }}
              ref={usernameInputRef}
            />
          }
        />
      </View>
      <View>
        <Text style={styles.label}>Password</Text>
        <Controller 
          name="password" 
          control={control} 
          rules= {{required: 'This is required'}}
          defaultValue=''
          render={(props) => 
            <TextInput {...props} 
              secureTextEntry={true}
              style={styles.textbox}
              onChangeText={(value) => {
                props.onChange(value)
              }}
              ref={passwordInputRef}
            />
          }
        />
      </View>
      <View>
        <TouchableOpacity 
          // handleSubmit validates inputs before calling onSubmit
          onPress={handleSubmit(onSubmit, onError)}
          // onPress={() => navigation.navigate('Main')}
          style={styles.buttonSpecial}>
          <Text style={styles.button}>Log In</Text>    
        </TouchableOpacity>
      </View>
      <View>
        <Text style={styles.label}> </Text>
        <TouchableOpacity onPress={signup}>
          <Text style={styles.buttonWhiteText}>New to Dasher? Create an account</Text>
        </TouchableOpacity>
      </View>
    </View>
  )
}

//Signup screen
function SignupScreen ({ navigation }) {

  // useForm allows us to validate inputs and build forms
  const {control, handleSubmit, setError, errors} = useForm( { criteriaMode: 'all' })
  const usernameInputRef = React.useRef()
  const passwordInputRef = React.useRef()
  const emailInputRef = React.useRef()

  const onSubmit = async (data) => { 
    // Once handleSubmit validates the inputs in onPress in button, this code is executed
    const json = JSON.parse(JSON.stringify(data))
    const username = json["username"]
    const password = json["password"]
    const email = json["email"]
    const newUser = {username, password, email}
    console.log(JSON.stringify(newUser))
    const response = await fetch("http://127.0.0.1:5000/signup", {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },
    body: JSON.stringify(newUser)
  }).then((response) => response.json())
  .then(data => {
      return data;
  });
  if (response.message == "user created") {
    navigation.navigate('Login');
    alert(`Account created! Please sign in`)
    //navigation.navigate('Main');
  } else {
    alert(`A problem occurred. Please try again`)
  }
  console.log(response)

}

const onError = (errors, e) => console.log(errors, e)

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Create Account</Text>

      <View>
        <Text style={styles.label}>Username</Text>
        <Controller 
          name="username" 
          control={control} 
          rules= {{required: 'This is required'}}
          defaultValue=''
          render={(props) => 
            <TextInput {...props} 
              //I know the following line sometimes gives a warning.
              //Please leave it in place, otherwise the forms are hard to work with
              autoCapitalize={false}
              style={styles.textbox}
              onChangeText={(value) => {
                props.onChange(value)
              }}
              ref={usernameInputRef}
            />
          }
        />
      </View>
      <View>
        <Text style={styles.label}>Password</Text>
        <Controller 
          name="password" 
          control={control} 
          rules= {{required: 'This is required'}}
          defaultValue=''
          render={(props) => 
            <TextInput {...props} 
              secureTextEntry={true}
              style={styles.textbox}
              onChangeText={(value) => {
                props.onChange(value)
              }}
              ref={passwordInputRef}
            />
          }
        />
      </View>
      <View>
        <Text style={styles.label}>Email</Text>
        <Controller 
          name="email" 
          control={control} 
          rules= {{required: 'This is required'}}
          defaultValue=''
          render={(props) => 
            <TextInput {...props} 
              //I know the following line sometimes gives a warning.
              //Please leave it in place, otherwise the forms are hard to work with
              autoCapitalize={false}
              secureTextEntry={false}
              style={styles.textbox}
              onChangeText={(value) => {
                props.onChange(value)
              }}
              ref={emailInputRef}
            />
          }
        />
      </View>
      <View>
        <TouchableOpacity 
          // handleSubmit validates inputs before calling onSubmit
          onPress={handleSubmit(onSubmit, onError)}
          // onPress={() => navigation.navigate('Main')}
          style={styles.buttonSpecial}>
          <Text style={styles.button}>Sign up</Text>    
        </TouchableOpacity>
      </View>
    </View>
  )
}

// Main menu screen
function MainScreen ({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>
      Main Menu</Text>

      <TouchableOpacity
        onPress={() => navigation.navigate('Recommendations')}
        style={styles.buttonBasic}>
        <Text style={styles.button}>Get recommendation</Text>
      </TouchableOpacity>

      <TouchableOpacity
