import 'react-native-gesture-handler'
import { useForm, Controller } from 'react-hook-form'
import React, { useEffect, useState, Component } from 'react'
import { StyleSheet, Button, Text, View, Alert, TextInput, TouchableOpacity, FlatList} from 'react-native'

export function CommentsScreen ({ navigation }) {
    useEffect (async () => {
        const response = await fetch("http://localhost:5000/get_comments", {
          method: "GET",
          headers: {
            "Content-Type": "application/json"
          }
        })
        console.log(response);
    })
    const list = response.json().comments
    console.log(list)
    return (
        <View style={styles.container}>

        </View>
    );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#66cc99',
    alignItems: 'center',
    justifyContent: 'center'
  },
  textbox: {
      backgroundColor: 'rgba(255,255,255,.5)',
        height: 40,
        width: 200,
        marginTop: 10,
        marginBottom: 20,
        borderRadius: 5,
        borderColor: 'white',
        borderWidth: 1,
        padding: 5
  },
  title: {
      color: 'black',
      fontSize: 30,
      // backgroundColor: 'white',
      marginHorizontal: 15,
      marginVertical: 15,
      padding: 7,
      paddingHorizontal: 20,
      borderRadius: 5
  },
})
