import 'react-native-gesture-handler'
import { useForm, Controller } from 'react-hook-form'
import { StatusBar } from 'expo-status-bar'
import React, { useEffect, useState } from 'react'
import { StyleSheet, Button, Text, View, Image, Alert, SafeAreaView, TextInput, TouchableOpacity, AppRegistry } from 'react-native'
import { NavigationContainer } from '@react-navigation/native'
import { createStackNavigator } from '@react-navigation/stack'
import { RecommendForm } from './RecommendForm'

export default function App () {
  return (
	<NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Login" component={LoginScreen} />
        <Stack.Screen name="Main" component={MainScreen} />
        <Stack.Screen name="Recommendations" component={RecommendScreen} />
        <Stack.Screen name="RecordDrive" component={RecordDriveScreen} />
        <Stack.Screen name="Statistics" component={StatisticsScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  )
}

const Stack = createStackNavigator()

// Login screen
function LoginScreen ({ navigation }) {
  const [username, setUsername] = useState([])



  return (
    <View style={styles.container}>
      <Text style={styles.title}>
      Welcome to Dasher!</Text>

      <TextInput
      style={styles.textbox}
      placeholder = "Username" placeholderTextColor = 'rgba(0,0,0,0.5)'
      onChangeText = {(text) => setUsername(text)}
      />

      <TextInput
      secureTextEntry={true}
      style={styles.textbox}
      placeholder = "Password" placeholderTextColor = 'rgba(0,0,0,0.5)'
      />

      <TouchableOpacity
        // onPress={() => alert(`You are logged in as ${username}`)}
        onPress={() => navigation.navigate('Main')}
        style={{ backgroundColor: '#fff' }}>
        <Text style={styles.button}>Login</Text>
      </TouchableOpacity>

      <StatusBar style="auto" />
    </View>

  )
}

// Main menu screen
function MainScreen ({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.container}>
      Main Menu</Text>

      <TouchableOpacity
        onPress={() => navigation.navigate('Recommendations')}
        style={{ backgroundColor: '#fff' }}>
        <Text style={styles.button}>Get recommendation</Text>
      </TouchableOpacity>

      <TouchableOpacity
        onPress={() => navigation.navigate('RecordDrive')}
        style={{ backgroundColor: '#fff' }}>
        <Text style={styles.button}>Record a new drive</Text>
      </TouchableOpacity>

      <TouchableOpacity
        onPress={() => navigation.navigate('Statistics')}
        style={{ backgroundColor: '#fff' }}>
        <Text style={styles.button}>View statistics</Text>
      </TouchableOpacity>

      <TouchableOpacity
        // onPress={() => navigation.navigate('Recommendations')}
        style={{ backgroundColor: '#fff' }}>
        <Text style={styles.button}>View/edit past drives</Text>
      </TouchableOpacity>

    </View>
  )
}

// GetRecommendations screen
function RecommendScreen ({ navigation }) {
  const { register, setValue, handleSubmit, errors } = useForm();
  const onSubmit = data => Alert.alert('Form Data', data);

  React.useEffect(() => {
    register({ name: 'firstName'}, { required: true });
    register({ name: 'lastName'});
  }, [register])

  console.log(errors)

  return (
    <View style={styles.container}>
      <Text style={styles.label}>First name</Text>
      <TextInput
        style={styles.input}
        onChangeText={text => setValue('firstName', text, true)}
      />
      <Text style={styles.label}>Last name</Text>
      <TextInput
        style={styles.input}
        onChangeText={text => setValue('lastName', text)}
      />

      <View style={styles.button}>
        <Button color="white" title="Button" onPress={handleSubmit(onSubmit)} />
      </View>
    </View>
  );
