  const [prediction, setPrediction] = useState(0.0);

  return (

    <View style={styles.container}>
      <Text style={styles.title}> 
      Get Recommendation</Text>

      <TextInput
      style={styles.textbox}
      placeholder = "Restaurant name" placeholderTextColor = 'rgba(0,0,0,0.5)'
      //onChangeText = {(text) => setUsername(text)}
      />

      <TextInput
      style={styles.textbox}
      placeholder = "Driving distance" placeholderTextColor = 'rgba(0,0,0,0.5)'
      //onChangeText = {(text) => setUsername(text)}
      />

      <TextInput
      style={styles.textbox}
      placeholder = "Expected payment" placeholderTextColor = 'rgba(0,0,0,0.5)'
      //onChangeText = {(text) => setUsername(text)}
      />

      <TouchableOpacity
        //onPress={() => navigation.navigate('Statistics')}
        // onPress={() => }
        style={{ backgroundColor: '#fff' }}>
        <Text style={styles.button}>Go</Text>
      </TouchableOpacity>

    </View>

  );*/
}

// RecordDrive screen
function RecordDriveScreen ({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>
      Record Drive</Text>

      <Text style = {styles.text}>Time</Text>
      <Text style = {styles.time}>00:00:00</Text>
      <Image source={{ uri: 'https://upload.wikimedia.org/wikipedia/commons/thumb/b/b4/1D_line.svg/2000px-1D_line.svg.png' }}
        style={{ width: 400, height: 30 }} />
      <Text style = { styles.text }>Press the record button to start your drive!</Text>
      <StatusBar style="auto" />

      <TouchableOpacity style = {styles.button}
        // onPress={startButton}
        style={{ backgroundColor: 'white' }}>
        <Text style={ styles.button}>Start Recording</Text>
      </TouchableOpacity>

      <TouchableOpacity style = {styles.button}
        // onPress={saveDrive}
        style={{ backgroundColor: 'white' }}>
        <Text style={ styles.button}>Save Drive</Text>
      </TouchableOpacity>

      <TouchableOpacity style = {styles.button}
        // onPress={deleteDrive}
        style={{ backgroundColor: 'white' }}>
        <Text style={ styles.button}>Delete Drive</Text>
      </TouchableOpacity>

    </View>
  )
}

// Statistics screen
function StatisticsScreen ({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>
      Statistics</Text>

    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#1ddf6e',
    alignItems: 'center',
    justifyContent: 'center'
  },
  title: {
    color: 'black',
    fontSize: 30,
    // backgroundColor: 'white',
    marginHorizontal: 15,
    marginVertical: 20,
    padding: 7,
    paddingHorizontal: 20,
    borderRadius: 5
  },
  button: {
    color: 'black',
    fontSize: 20,
    marginHorizontal: 10,
    marginVertical: 10,
    borderRadius: 5
  },
  textbox: {
  	backgroundColor: 'rgba(0,0,0,0.1)',
  	height: 40,
  	width: 200,
  	marginTop: 0,
  	marginBottom: 20,
  	borderRadius: 5,
  	borderColor: 'white',
  	borderWidth: 1,
  	padding: 5
  },

  text: {
    fontSize: 20,
    color: 'black',
    margin: 10,
    alignContent: 'center'
  },
  time: {
    fontWeight: 'bold',
    fontSize: 80,
    color: '#000',
    alignContent: 'center'
  },
  input: {
    backgroundColor: 'white',
    height: 40,
    padding: 10,
    borderRadius: 4,
  }
})


